// Problem count by status
fetch dt.davis.problems, from: now() - 7d
| summarize count = count(), by: {status}
| sort count desc

// Problems by day
fetch dt.davis.problems, from: now() - 7d
| fieldsAdd day = bin(timestamp, 1d)
| summarize problem_count = count(), by: {day}
| sort day desc
